    //Function to return the maximum XOR value.
    maximumXor(N, Q, arr, queries)
    {
        arr.sort((a, b) => a - b); // sorting the array in ascending order
        let answer = [];
        for(let i=0;i<Q;i++){
            let x = queries[i][0];
            let m = queries[i][1];
            let start = 0, stop = this.bisectRight(arr, m, 0, N);
            let num = 0;
            for(let j=30;j>=0;j--){
                let cut = this.bisectLeft(arr, num + (1<<j), start, stop);
                if(cut > start && (x & (1<<j))){
                    stop = cut;
                }else if(cut < stop){
                    start = cut;
                    num += (1<<j);
                }
            }
            answer.push(start < stop ? num ^ x : -1);
        }
        return answer;
    }

    bisectRight(arr, target, start, end){
        while(start < end){
            let mid = Math.floor((start+end)/2);
            if(arr[mid] <= target){
                start = mid + 1;
            }else{
                end = mid;
            }
        }
        return start;
    }

    bisectLeft(arr, target, start, end){
        while(start < end){
            let mid = Math.floor((start+end)/2);
            if(arr[mid] < target){
                start = mid + 1;
            }else{
                end = mid;
            }
        }
        return start;
    }
}
