// Comparator function for qsort to sort integers in non-decreasing order
int cmp(const void* a, const void* b) {
    // Dereference the void pointers to int pointers and then get the integer values
    return (*(int*)a - *(int*)b);
}

// Function to merge two sorted arrays without using extra space
void mergeArrays(int* a, int n, int* b, int m) {
    // Initialize pointer 'i' to the last index of array 'a'
    int i = n - 1;
    // Initialize pointer 'j' to the first index of array 'b'
    int j = 0;

    // Loop to traverse through both arrays from the end of 'a' and start of 'b'
    while (i >= 0 && j < m) {
        // If the current element in 'a' is smaller than the current element in 'b',
        // it means all elements before 'a[i]' are already smaller, so move to the
        // previous element in 'a'
        if (a[i] < b[j]) {
            i--;
        } else {
            // If 'a[i]' is greater than or equal to 'b[j]', swap them
            int temp = b[j];
            b[j] = a[i];
            a[i] = temp;

            // After swapping, move to the next element in 'b' and the previous element
            // in 'a'
            i--;
            j++;
        }
    }

    // After swapping, the arrays may not be fully sorted.
    // Therefore, sort both arrays individually to maintain the sorted order.
    qsort(a, n, sizeof(int), cmp);
    qsort(b, m, sizeof(int), cmp);
}

class Solution {
  public:
    void mergeArrays(vector<int>& a, vector<int>& b) {
        // Initialize pointer 'i' to the last index of array 'a'
        int i = a.size() - 1;
        // Initialize pointer 'j' to the first index of array 'b'
        int j = 0;

        // Loop until either 'i' goes out of bounds of 'a' or 'j' exceeds bounds of 'b'
        // Also, continue looping as long as the current element in 'a' is greater than
        // the current element in 'b'
        while (i >= 0 && j < b.size() && a[i] > b[j]) {
            // Swap the elements a[i] and b[j]
            swap(a[i], b[j]);
            // Decrement 'i' to move to the previous element in 'a'
            i--;
            // Increment 'j' to move to the next element in 'b'
            j++;
        }

        // After swapping, sort array 'a' to ensure its elements are in non-decreasing
        // order
        sort(a.begin(), a.end());
        // Similarly, sort array 'b' to ensure its elements are in non-decreasing order
        sort(b.begin(), b.end());
    }
};

class Solution {
    // Function to merge the arrays.
    public void mergeArrays(int a[], int b[]) {
        // code here
        int i = a.length - 1;
        int j = 0;

        // Swap elements to ensure that all elements in 'a' are smaller than those in
        // 'b'
        while (i >= 0 && j < b.length && a[i] > b[j]) {
            // Swap a[i] and b[j]
            int temp = a[i];
            a[i] = b[j];
            b[j] = temp;
            // Move the pointers
            i--;
            j++;
        }

        // Sort both arrays to maintain the sorted order
        Arrays.sort(a);
        Arrays.sort(b);
    }
}

class Solution {
    mergeArrays(a, b) {
        // Initialize pointer 'i' to the last index of array 'a'
        let i = a.length - 1;
        // Initialize pointer 'j' to the first index of array 'b'
        let j = 0;

        // Loop to traverse through both arrays from the end of 'a' and start of 'b'.
        // Continue looping as long as:
        // - 'i' is within the bounds of array 'a'.
        // - 'j' is within the bounds of array 'b'.
        // - The current element in 'a' is greater than the current element in 'b'.
        while (i >= 0 && j < b.length && a[i] > b[j]) {
            // Swap the elements a[i] and b[j] using array destructuring
            [a[i], b[j]] = [ b[j], a[i] ];
            // Decrement 'i' to move to the previous element in 'a'
            i--;
            // Increment 'j' to move to the next element in 'b'
            j++;
        }

        // After swapping, the arrays may not be fully sorted.
        // Therefore, sort both arrays individually to maintain the sorted order.
        a.sort((x, y) => x - y);
        b.sort((x, y) => x - y);
    }
}
