bool decode (string s)
{
    string find = "hello";
    int j = 0;

    int i = 0, n = s.length ();
    while (i < n)
    {
        if (s[i] == find[j])
        {
            j++;
            // if we reach the end of the string "hello",
            // means we found all the characters in the 
            // required sequence
            if (j == 5) return 1;
        }
        i++;
    }
    return 0;
}
