// User function Template for C++

class Solution {
  public:
    // Function to perform swap operation
    void middleSwap(int arr[4][4]) {
        // Swap middle rows (2nd row with 3rd row)
        for (int j = 0; j < 4; j++) {
            swap(arr[1][j], arr[2][j]);
        }

        // Swap middle columns (2nd column with 3rd column)
        for (int i = 0; i < 4; i++) {
            swap(arr[i][1], arr[i][2]);
        }
    }
};
