class Solution {
  public:
    //Function to find the minimum number of operations required to make both the numbers equal.
    int solve(int a, int b) {
        //if both numbers are already equal, return 0.
        if (a == b)
            return 0;
        //if the bitwise AND of a and b is equal to the smaller of the two numbers, 
        //return 1 as only one operation (AND) is needed to make them equal.
        else if ((a & b) == min(a, b))
            return 1;
        //otherwise return 2 as both a and b are different and 
        //at least two operations are needed to make them equal.
        else
            return 2;
    }
};

