//Back-end complete function Template for C++

class Solution{   
public:
    // Function to find minimum value of x such that ((a * x^2) + (b * x) + c) >= k
    int minX(int a, int b, int c, long int k) { 
        int lo = 0, hi = 1e5, ans = 0;

        // Binary search until lo <= hi
        while(lo <= hi){
            int mid = (lo + hi) >> 1; // Calculating mid value
            long long val = (long long)a * mid * mid; // Calculating the equation value at mid
            val += (long long)b * mid + c; // Add rest of the terms to val

            // If val >= k, update ans and search in the lower range
            if(val >= k){
                ans = mid;
                hi = mid - 1;
            }

            // If val < k, search in the higher range
            else lo = mid + 1;
        }

        // Return the minimum value of x
        return ans;
    } 
};

class Solution
{
    public int minX(int a, int b, int c, long k)
    {
        int lo = 0, hi = 100000, ans = 0; // Changed from 1e5 which is a double to 100000 which is an int

        while (lo <= hi)
        {
            int mid = (lo + hi) >> 1;
            long val = (long)a * mid * mid; // Explicit cast to long
            val += (long)b * mid + c; // Explicit cast to long

            if (val >= k)
            {
                ans = mid;
                hi = mid - 1;
            }
            else
            {
                lo = mid + 1;
            }
        }

        return ans;
    }
}
