// Function to calculate the alternate product of the array elements
class Solution {
  public:
    int altProduct(vector<int>& arr) {
        int n = arr.size();
        // Sorting the array in ascending order
        sort(arr.begin(), arr.end());

        // Initializing the sum variable
        int sum = 0;

        // Calculating the sum of alternate products
        for (int i = 0; i < n / 2; i++)
            sum += (arr[i] * arr[n - i - 1]);

        // Returning the final sum
        return sum;
    }
};

class Solution {
    public int altProduct(int[] arr) {
        int n = arr.length;

        // Sorting the array in ascending order
        Arrays.sort(arr);

        // Initializing the sum variable
        int sum = 0;

        // Calculating the sum of alternate products
        for (int i = 0; i < n / 2; i++) {
            sum += arr[i] * arr[n - i - 1];
        }

        // Returning the final sum
        return sum;
    }
}

