//Back-end complete function Template for C++

class Solution {
  public:
    //Function to find the minimum integer that can be multiplied by N to obtain a sum greater than or equal to the sum of all elements in the array.
    int minimumInteger(int N, vector<int> &A) {
        //Initializing mmin as a large value.
        int mmin = 1e9;
        //Initializing sum as 0.
        long long sum = 0;

        //Calculating the sum of all elements in the array.
        for(int i:A)
            sum += i;

        //Checking each element in the array.
        for (int i:A)
        {
            //Checking if i multiplied by N is greater than or equal to the sum of all elements in the array.
            //If true, updating mmin with the minimum value between mmin and i.
            if (i*1LL*N >= sum)
            {
                mmin = min(mmin, i);
            }
        }
        //Returning mmin, i.e., the minimum integer that can be multiplied by N to obtain a sum greater than or equal to the sum of all elements in the array.
        return mmin;
    }
};

class Solution {
    public static int minimumInteger(int N, int[] A) {
        // code here
        int mmin = Integer.MAX_VALUE;
        long sum = 0;
        for (int i : A) sum += i;
        for (int X : A)
            if (((long)X * N) >= sum && mmin > X) {
                mmin = X;
            }
        return mmin;
    }
}

//User function Template for C#

class Solution
{
    //Complete this function
    public int minimumInteger(int N, List<int> A)
    {
        //Initializing mmin as a large value.
        int mmin = 1000000000;
        //Initializing sum as 0.
        long sum = 0;

        //Calculating the sum of all elements in the array.
        foreach(int i in A)
            sum += i;

        //Checking each element in the array.
        foreach(int i in A)
        {
            //Checking if i multiplied by N is greater than or equal to the sum of all elements in the array.
            //If true, updating mmin with the minimum value between mmin and i.
            if (i*1L*N >= sum)
            {
                mmin = Math.Min(mmin, i);
            }
        }
        //Returning mmin, i.e., the minimum integer that can be multiplied by N to obtain a sum greater than or equal to the sum of all elements in the array.
        return mmin;
    }
}
