class Solution {
    //Function to find minimum number of elements to be inserted in the array
    minimumNumber(n, arr)
    {
        //function to calculate gcd of two numbers.
        const gcd = (a, b) => {
            if (b === 0) {
                return a;
            }
            return gcd(b, a % b);
        }

        let res = 0;
        //iterating over the array.
        for (let i = 0; i < n; i++) {
