// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the rotation count
    int rotationCount(long long R, long long D)
    {
        int rotation = 0;

        // Loop until both numbers are greater than 0
        while(D > 0 && R > 0){
            int x = D % 10; // Extract last digit of D
            int y = R % 10; // Extract last digit of R
            x = abs(x - y); // Calculate absolute difference
            if(x > 5)
                x = 10 - x; // Choose the smaller rotation, 1 or 9
            rotation += x; // Add the rotation to the total count
            D /= 10; // Remove the last digit of D
            R /= 10; // Remove the last digit of R
        }
        return rotation; // Return the total rotation count
    }
};

