//Back-end complete function Template for C++

class Solution{
public:
    //Function to find the minimum sum of absolute differences between corresponding elements of two arrays.
    long long findMinSum(vector<int> &A,vector<int> &B,int N){
        long long sum=0;
        //sorting both arrays in ascending order.
        sort(A.begin(),A.end());
        sort(B.begin(),B.end());

        //calculating sum of absolute differences of corresponding elements.
        for(int i=0;i<N;i++){
            sum+=abs(A[i]-B[i]);
        }
        return sum;
    }
};

//Back-end complete function Template for Java

class Solution { 
    // function to find the minimum sum
    long findMinSum(int[] A,int[] B,int N) { 
        // initializing the sum
        long sum=0;
        // sorting array A
        Arrays.sort(A);
        // sorting array B
        Arrays.sort(B);
        // calculating the sum of absolute differences
        for(int i=0;i<N;i++){
            sum+=Math.abs(A[i]-B[i]);
        }
        // returning the sum
        return sum;
    }
}

class Solution {
    //Function to find the minimum sum of absolute differences of two arrays.
    findMinSum(A, B, N) {
        let sum = 0;
        //sorting both arrays in ascending order.
        A.sort((a, b) => a - b);
        B.sort((a, b) => a - b);
        //iterating over both arrays to calculate the sum of absolute differences.
        for (let i = 0; i < N; i++) {
            sum += Math.abs(A[i] - B[i]);
        }
        return sum;
    }
}

