// Back-end complete function Template for C++

class Solution{
public:
    // Function to find the minimum number of toggles required
    int minToggle(int n, int arr[]){
        // Array to store the count of zeros at each index
        int zero[n+1];
        zero[0] = 0;

        // Counting zeros at each index
        for (int i = 1; i <= n; ++i){
            if (arr[i-1] == 0)
                zero[i] = zero[i-1] + 1;
            else
                zero[i] = zero[i-1];
        }

        // Initializing answer as the maximum possible value
        int ans = n;

        // Finding the minimum number of toggles
        for (int i=1; i <= n; ++i)
            ans = min(ans, i-zero[i]+zero[n]-zero[i]);

        // Returning the minimum number of toggles
        return ans;
    }
};
