class Solution {
  public:
    int minimum_vertical_sum(vector<vector<int>>& arr, int n) {
        // Find the maximum number of columns
        int maxColumns = 0;
        for (const auto& row : arr) {
            maxColumns = max(maxColumns, (int)row.size());
        }

        // Initialize a vector to store vertical sums
        vector<int> verticalSums(maxColumns, 0);

        // Calculate vertical sums column by column
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < arr[i].size(); j++) {
                verticalSums[j] += arr[i][j];
            }
        }

        // Find the minimum vertical sum
        return *min_element(verticalSums.begin(), verticalSums.end());
    }
};
