// Back-end complete function Template for C++

class Solution {
  public:
    int minVal(int a, int b) {
        // Count of set-bits in bit
        int setBits = __builtin_popcount(b);
        int setBits1 = __builtin_popcount(a);
        int ans = 0;

        for (int i = 0; i <= 31; i++) {
            int mask = 1 << i;
            int set = a & mask;

            // If i'th bit is set also set the
            // same bit in the required number
            if (set == 0 && setBits > setBits1) {
                ans |= (mask);
                setBits--;
            } else if (set && setBits1 > setBits) {
                setBits1--;
            } else {
                ans |= set;
            }
        }

        return ans;
    }
};

class Solution {
    public static int minVal(int a, int b) {
        // code here
        int setBits = Integer.bitCount(b);
        int setBits1 = Integer.bitCount(a);
        int ans = 0;

        for (int i = 0; i <= 31; i++) {
            int mask = 1 << i;
            int set = a & mask;

            // If i'th bit is set also set the
            // same bit in the required number
            if (set == 0 && setBits > setBits1) {
                ans |= (mask);
                setBits--;
            } else if (set != 0 && setBits1 > setBits) {
                setBits1--;
            } else {
                ans |= set;
            }
        }

        return ans;
    }
}

class Solution {
    //Function to find minimum of two numbers
    minVal(a, b)
    {
        let setBits = b.toString(2).split('1').length - 1;
        let setBits1 = a.toString(2).split('1').length - 1;
        let ans = 0;

        for (let i = 0; i < 32; i++) {
            let mask = 1 << i;
            let set = a & mask;

            if (set === 0 && setBits > setBits1) {
                ans |= mask;
                setBits -= 1;
            } else if (set !== 0 && setBits1 > setBits) {
                setBits1 -= 1;
            } else {
                ans |= set;
            }
        }

        return ans;
    }
}
