        // The element just before mid is missing
        if (mid > 0 && arr[mid] - arr[mid - 1] !== diff) return arr[mid - 1] + diff;

        // If the elements till mid follow AP, then recur
        // for right half
        if (arr[mid] === arr[0] + mid * diff)
            return this.findMissingUtil(arr, mid + 1, high, diff);

        // Else recur for left half
        return this.findMissingUtil(arr, low, mid - 1, diff);
    }

    findMissing(arr) {
        const n = arr.length;
        const diff = (arr[n - 1] - arr[0]) / n;
        return this.findMissingUtil(arr, 0, n - 1, diff);
    }
