class Solution {
  public:
    // Function to find the missing element in array b
    int findMissing(vector<int>& arr1, vector<int>& arr2) {
        int n = arr1.size();
        int suma = 0;

        // XOR operation to calculate the sum of elements in array a
        for (int i = 0; i < n; i++) {
            suma ^= arr1[i];
        }

        // XOR operation to calculate the sum of elements in array b
        for (int i = 0; i < n - 1; i++) {
            suma ^= arr2[i];
        }

        // Return the missing element
        return suma;
    }
};

