// Back-end complete function Template for C++

class Solution{
public:
    //Function to calculate the modified Fibonacci series.
    int modifiedFib(int N, int A, int B, int C)
    {
        int fib[6], mod = 1000000007;
        //Calculating the first 6 terms of the modified Fibonacci series.
        fib[1] = A + B;
        fib[2] = B + C;
        fib[3] = C - A;
        fib[4] = -1*(A + B);
        fib[5] = -1*(B + C);
        fib[0] = -1*(C - A);

        //Calculating the solution for the given input N using modular arithmetic.
        int ans = (fib[N%6]%mod + 2*mod)%mod;
        return ans;
    }
};

