//Back-end complete function Template for Java

class GfG{
    public void count(long n){
        long s=0,c=0;
            while(n>0){
                if(n%2!=0){
                    s++; //increment s if n is odd
                }
                n>>=1; //right shift n by 1 bit
                c++; //increment c for each bit shifted
            }
            c-=s; //subtract s from c
            System.out.println(s+" "+c); //print the values of s and c
    }
}
