class Solution {
  public:
    // Function to determine the winner based on divisible counts
    string winner(vector<int> &arr, int m, int n) {
        int c1 = 0, c2 = 0;

        // Iterating over the array to count the number of divisibles
        for (auto &num : arr) {
            if (num % m == 0)
                c1++;
            else if (num % n == 0)
                c2++;
        }
        // Determining the winner based on the counts
        if (c1 > c2)
            return "Ram";
        if (c2 > c1)
            return "Rohan";
        return "Both";
    }
};

