//Back-end complete function Template for C++

class Solution{
	public:
   	//Function to find the closest number divisible by n to x.
   	int closestNo(int x, int n){
   		//calculating the lower and higher multiple of n closest to x.
   		int a = n * (x/n);
   		int b = n * ceil((x*1.0)/n);

   		//if a = 0, it means x is already divisible by n, so return b.
   		if(a == 0) return b;

   		//returning the closest multiple of n to x.
   		return abs(a - x) < abs(b - x) ? a : b;
   	}    
};

//User function Template for C#
class Solution
{
    //Complete this function
    public int closestNo(int x, int n)
    {
        //calculating the lower and higher multiple of n closest to x.
        int a = n * (x/n);
        int b = n * ((x/n) + 1);

        //if a = 0, it means x is already divisible by n, so return b.
        if(a == 0) return b;

        //returning the closest multiple of n to x.
        return Math.Abs(a - x) < Math.Abs(b - x) ? a : b;
    }
}
