class Solution
{
    //Complete this function
    public long sum(int N)
    {
        // count of numbers which are divisible by 3
        long cnt_3 = (long) (N - 1) / 3;
        // count of numbers which are divisible by 7
        long cnt_7 = (long) (N - 1) / 7;
        // count of numbers which are divisible by 21
        long cnt_21 = (long) (N - 1) / 21;
        // calculating sum of numbers which are divisible by 3 only
        long sum3 = (cnt_3 * (2 * 3 + (cnt_3 - 1) * 3)) / 2;
        // calculating sum of numbers which are divisible by 7 only
        long sum7 = (cnt_7 * (2 * 7 + (cnt_7 - 1) * 7)) / 2;
        // calculating sum of numbers which are divisible by 3 and 7 both
        long sum21 = (cnt_21 * (2 * 21 + (cnt_21 - 1) * 21)) / 2;
        // finding sum
        long ans = sum3 + sum7 - sum21;
        return ans;
    }
}
