class Solution {

    // method to calculate the product of all elements in the array
    public static int product(int arr[], int n) {
        int count = 1;
        for (int j = 0; j < n; j++) {
            // multiply each element with the current count
            count *= arr[j];
        }
        // return the final count
        return count;
    }
}
