void multiply(int A[][100], int B[][100], int C[][100], int N)
{
    int i, j, k;

    // nested loops to iterate over the matrices
    for (i = 0; i < N; i++)
    {
        for (j = 0; j < N; j++)
        {
            C[i][j] = 0;

            // inner loop to perform matrix multiplication
            for (k = 0; k < N; k++)
                C[i][j] += A[i][k]*B[k][j];
        }
    }
}

