class GfG{
  public static void multiply(int A[][], int B[][], int C[][], int N)
{
    //iterate over rows of matrix A
    for (int i = 0; i < N; i++)
    {
        //iterate over columns of matrix B
        for (int j = 0; j < N; j++)
        {
            //initialize element in matrix C to 0
            C[i][j] = 0;
            //iterate over common dimension of A and B
            for (int k = 0; k < N; k++)
                //multiply and add elements from A and B to C
                C[i][j] += A[i][k]*B[k][j];
        }
    }
}
}
