class Solution {
  public:
    // Function to multiply two matrices.
    bool multiplyMatrix(int mat1[4][4], int mat2[4][4], int result[4][4]) {

        // Perform matrix multiplication
        for (int i = 0; i < 4; i++) {
            for (int j = 0; j < 4; j++) {
                int sum = 0;
                // Calculating the sum of products of corresponding elements
                for (int k = 0; k < 4; k++) {
                    sum += mat1[i][k] * mat2[k][j];
                }
                // Storing the sum in the result matrix
                if (result[i][j] != sum)
                    return false;
            }
        }

        return true; // Indicate multiplication was successful
    }
};

