class Solution {
  public:
    long long nearestPower(long long N , long long M) {
        
        // Finding the power of M to get the nearest power less than N
        long long temp=floor(log(N)/log(M));
        
        // The number is calculating M to the power temp
        long long low = pow(M,temp);
        
        // The next power of M is calculated
        long long high = low*M;
        
        // The nearest between the two is found
        if(N-low < high-N)
            return low;
        else
            return high;
    }
};

class Solution
{
    public long nearestPower(long N, long M)
    {
        // Finding the power of M to get the nearest power less than N
        long temp = (long)Math.Floor(Math.Log(N) / Math.Log(M));

        // The number is calculating M to the power temp
        long low = (long)Math.Pow(M, temp);

        // The next power of M is calculated
        long high = low * M;

        // The nearest between the two is found
        if (N - low < high - N)
            return low;
        else
            return high;
    }
}
