//Back-end complete function Template for C++

/*Function to swap array elements
 * arr : array input
 * sizeof_array(N): number of elements in array
 */
class Solution {
  public:
    // Function to swap elements of the array
    void swapElements(vector<int> &arr) {
        int n = arr.size();
        for (int i = 0; i < n - 2; i++) {
            int temp = arr[i];
            arr[i] = arr[i + 2];
            arr[i + 2] = temp;
        }
    }
};

class Solution {
