//Back-end complete function Template for C++

vector<string> login_system(vector<string> &v) {
    vector<string> ans;

    int n;
    n = v.size();
    unordered_map<string, int> m1;

    int i;
    for (i = 0; i < n; i++) {
        string s;
        s = v[i];
        if (m1.find(s) == m1.end()) {
            m1[s] = 1;
            ans.push_back(s);
        } else {
            string w = s;
            w += to_string(m1[s]);
            ans.push_back(w);
            m1[s]++;
        }
    }

    return ans;
}
