import java.util.*;

public class Main {
    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        n++;
        while (true) {
            int i;
            for (i = 2; i < n; i++) {
                if (n % i == 0) break;
            }
            if (i == n) {
                System.out.println(i);
                break;
            }
            n++;
        }
    }
}
