class Solution{
public:
    // Function to find the nth non-Fibonacci number
    long long int nonFibonacci(int N){
        long long int a = 1, b = 2, c = 3;
        long long int n = N;

        // Loop until n is greater than 0
        while(n > 0){
            // Update the values of a, b, and c
            a = b;
            b = c;
            c = a + b;

            // Deduct the difference between c, b, and 1 from n
            n = n - (c - b - 1);
        }

        // Add the difference between c, b, and 1 to n
        n = n + (c - b - 1);

        // Return the nth non-Fibonacci number
        return (b + n);
    }
};
