//Back-end complete function Template for C++
class Solution {
  public:

    int nthEvenFibonacci(int n) {
        int a = 0, b = 1; // First two Fibonacci numbers
        int count = 0;

        while (true) {
            int c = a + b;
            a = b;
            b = c;

            // Check if the number is even
            if (c % 2 == 0) {
                count++;
            }

            // If we've found the nth even Fibonacci number
            if (count == n) {
                return c;
            }
        }
    }
};

