{
    public long findPosition(long N, long A)
    {
        //initializing variables for Fibonacci sequence
        long f1 = 0, f2 = 1, f3;
        //starting position of Fibonacci sequence
        long i = 2;

        //loop to generate Fibonacci sequence until it breaks
        while(i!=0)
        {
            //calculating next Fibonacci number
            f3 = f1 + f2;
            f1 = f2;
            f2 = f3;

            //checking if current Fibonacci number is divisible by A
            if(f2%A == 0)
            {
                //returning position of first number divisible by A
                return N*i;
            }

            //incrementing position
            i++;
        }
        //returning 0 if no number divisible by A is found
        return 0;
    }
}

