class Solution {
  public:
    // Function to calculate the n'th Pentagonal Number.
    long long getNthPentagonalNum(long long n) {

        // Calculate the n'th Pentagonal Number.
        long long ans = (3*n*n-n)/2;

        return ans;
    }
};

