      gcd(a, b) {
        while (b) { // Continue loop as long as b is not equal to 0.
            let t = a; // Create a temporary variable to store the value of a.
            a = b; // Assign the value of b to a.
            b = t % b; // Update the value of b to the remainder of t divided by b.
        }
        return a; // Return the greatest common divisor of a and b.
    }

    numGame(n) {
        let ans = 1; // Initialize a variable to store the answer.
        for (let i = 2; i <= n; i++) { // Iterate from 2 to n.
            ans = Math.floor((ans * i) / this.gcd(ans, i)) % 1000000007; // Update the answer using the formula and take modulo 1000000007.
        }
        return ans; // Return the final answer.
    }
}

