    public long numGame(long n)
    {
        long ans = 1;
        long mod = 1000000007;
        for (long i = 2; i <= n; i++)
        {
            long x = gcd(ans, i);
            ans *= i;
            ans %= mod;
            ans *= pow2(x, mod - 2, mod);
            ans %= mod;
        }

        return ans;
    }

    public long pow2(long a, long b, long m)
    {
        long ans = 1;
        a %= m;
        while (b > 0)
        {
            if ((b & 1) != 0)
            {
                ans *= a;
                ans %= m;
            }
            a *= a;
            a %= m;
            b >>= 1;
        }

        return ans;
    }

    public long gcd(long a, long b) 
    {
        while (b != 0)
        {
            long t = a;
            a = b;
            b = t % b;
        }

        return a;
    }
}
