class Solution{
	public:
	// Function to calculate the number of diagonals in a polygon with n sides
	long long diagonals(int n)
	{
		// Formula to calculate the number of diagonals: n(n-3)/2
		// Using long long to handle larger values
		return ((long long)n*(n-3))/2;
	}
};

