//User function Template for C#

class Solution
{
    //Complete this function
    public long diagonals(int n)
    {
        // Formula to calculate the number of diagonals: n(n-3)/2
        // Using long to handle larger values
        return (long)n * (n - 3) / 2;
    }
}
