// Function to calculate the number of integer solutions for a given N
class Solution {
  public:
    long long noOfIntSols(long long N) {

        // Calculate the sum of the first N natural numbers and add 1 to it
        long long ans = ((N+1)*(N+2))/2;

        // Return the result
        return ans;
    }
};

