class Solution {
  public:
    //Function to calculate the number of ways. 
    long long numOfWays(int K) {
        
        //variable to store the final answer.
        long long ans = 0;
        
        //checking if the input is even.
        if(K%2==0)
            //calculating the number of ways for even input.
            ans = (K/2)*((K-2)/2);
        
        //if the input is odd.
        else
            //calculating the number of ways for odd input adding 1 to account for the middle number.
            ans = ((K+1)/2)*((K-3)/2) + 1;
        
        //returning the final answer.
        return ans;
    }
};

