class Solution {
  public:
    long long noOfNumbers(long long N) {
        
        //Calculating the number of multiples of 2, 3, 5, 7 
        //by using integer division and subtracting the count from N.
        long long ans = N - N/2 - N/3 - N/5 - N/7 + N/6 + N/10 + N/14 + N/15
                        + N/21 + N/35 - N/30 - N/42 - N/70 - N/105 + N/210;
                        
        return ans;
    }
};

