class Solution{
	public:
	int CountNo(int N)
	{
		int ans = 0;
		// Iterating for every number
		for(int i = 1; i <= N; i++){
			int cur = i;
			while(cur){
				ans += cur%10 == 0;
				// If got '0' then break
				if(cur % 10 == 0)
					break;
				cur /= 10;
			}
		}
		// return total count
		return ans;
	}
};

class Solution
{
    public int CountNo(int N)
    {
        int ans = 0;
        // Iterating for every number
        for (int i = 1; i <= N; i++)
        {
            int cur = i;
            while (cur != 0)
            {
                ans += (cur % 10 == 0) ? 1 : 0;
                // If got '0' then break
                if (cur % 10 == 0)
                    break;
                cur /= 10;
            }
        }
        // return total count
        return ans;
    }
}
