//Back-end complete function template in C++

class Solution{
    public:
    //map to store numbers with no consecutive ones
    map<int,int> h;

    //helper function to generate numbers with no consecutive ones
    void helper(int n, vector<int> sol)
    {   
        //check if the current number is valid
        if (sol.size() <= n) 
        {
            int ans = 0;
            //calculate the decimal equivalent of the current number and insert into the map
            for (int i = 0; i < sol.size(); i++)
                ans += pow((double)2, i) * sol[sol.size() - 1 - i];
            h[ans] = 1;

            int last_element = sol[sol.size() - 1];
            //if the last element is 1, add 0 and make a recursive call
            if (last_element == 1) 
            {
                sol.push_back(0);
                helper(n, sol);
            }
            //if the last element is 0, add both 1 and 0 and make recursive calls
            else
            {
                sol.push_back(1);
                helper(n, sol);
                sol.pop_back();
                sol.push_back(0);
                helper(n, sol);
            }
        }
    }

    //function to generate numbers with no consecutive ones
    vector<int>numberWithNoConsecutiveOnes(int n)
    {
        vector<int>sol;
        sol.push_back(1);
        helper(n, sol);

        sol.clear();
        //copy the valid numbers from the map to the solution vector
        for(auto i: h)
            sol.push_back(i.first);

        //return the solution vector
        return sol;
    }
};
