//Back-end complete function template for C++

class Solution{   
public:
    // Function to get the first digit of a number
    int getFirstDigit(int x)
    {
        while (x >= 10)
            x /= 10;
        return x;
    }

    // Function to calculate the number of special numbers between L and R
    int abcd(int x)
    {
        // If x is a single digit number, return x
        if (x < 10)
            return x;

        // Calculate the number of tens and add 9 to it
        int tens = x / 10;
        int res = tens + 9;

        // Get the first and last digit of x
        int firstDigit = getFirstDigit(x);
        int lastDigit = x % 10;

        // If the last digit is smaller than the first digit, subtract 1 from res
        if (lastDigit < firstDigit)
            res--;

        return res;
    }

    // Function to find the number of special numbers in the range [L, R]
    int numbersInRange(int L, int R){
        // Return the difference between the number of special numbers for R and L-1
        return max(abcd(R) - abcd(L - 1), 0);
    }
};

