//Back-end complete function template for C++

class Solution{   
public:
    //Function to count the number of integers from 1 to N that have an odd number of divisors.
    int oddNumberOfDivisor(int N){

        int count = 0;
        //iterating from 1 to N
        for (int i = 1; i<=N; i++)
        {
            //checking if i is a perfect square
            int r = sqrt(i);
            if (r*r == i)
              count++; //incrementing count if i is a perfect square
        }
        return count;
    }
};

