//Back-end complete function template for C++

class Solution {
  public:
    // Function to check if a number is odd or even.
    bool isEven(int N) {
        if (N % 2 == 0)  // checking if the number is divisible by 2
            return true; // returning "even" if the number is even.
        else
            return false; // returning "odd" if the number is odd.
    }
};

