class Solution {

    // Function to determine if a number is odd or even
    isEven(N) {

        // Check if the number is divisible by 2
        if (N % 2 === 0) {
            return true; // Return true if the number is divisible by 2
        }

        return false; // Return false if the number is not divisible by 2
    }
}
