import heapq


class Geeks:

    # Function to insert element into the queue (max heap)
    def insert(self, q, k):
        heapq.heappush(q, -k)

    # Function to check if k is in q
    def find(self, q, k):
        return -k in q

    # Function to delete the max element from queue
    def delete(self, q):
        return -heapq.heappop(q)
