// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the optimal walk distance
    long long optimalWalk(long long N, long long A, long long B) 
    {
        // Initialize an array to store the optimal walk distance for each position
        long long dp[N+1];
        dp[1] = A;

        // Iterating over each position
        for(int i = 2; i <= N; i++)
        {
            // Calculate the minimum distance by considering different possibilities
            dp[i] = min(dp[i/2+(i%2)] + B + A*(i%2), min(dp[i/2] + B + (i%2)*A, dp[i-1] + A));
        }

        // Return the optimal walk distance for N positions
        return dp[N];
    }
};

