//User function Template for javascript
/**
 * @param {number} N
 * @param {number} A
 * @param {number} B
 * @returns {number}
*/

class Solution {
    //Function to find the minimum number of steps.
    optimalWalk(N, A, B) {
        let dp = new Array(N+1).fill(0); // initialize a list dp with length N+1, all elements are 0
        dp[1] = A; // set dp[1] as value A
        for(let i = 2; i <= N; i++) { // iterate from 2 to N+1
            dp[i] = Math.min(dp[Math.floor(i/2)+(i%2)]+B+A*(i%2), Math.min(dp[Math.floor(i/2)]+B+(i%2)*A, dp[i-1]+A)); // calculate dp[i] using three different formulas
        }
        return dp[N]; // return the value of dp[N]
    }
}
