//Back-end complete function Template for C++

void sum(vector<pair<int,int>>v)
{
    long long sum=0; //initialize the sum variable to 0
    for(auto it=v.begin();it!=v.end();it++) //iterate through the vector
    {
       sum+=it->second; //add the second element of each pair to the sum
    }
    cout<<sum; //output the sum
    cout<<endl; //output a new line character
}
